<script>function refund(stripe_id){    $.post( "<?php echo Config::$base_url ?>/paynow/refund.php",{stripe_id:stripe_id}).done(function(realb){                 data=jQuery.parseJSON(realb);              if(data.status==true){                $("#message").addClass("alert alert-success").removeClass("alert-danger");                $("#message").html(data.message);                $("#message").fadeTo(2000, 500).slideUp(500);                table.ajax.reload();            }else{                $("#message").addClass("alert alert-danger").removeClass("alert-success");                $("#message").html("Error : "+data.message);                }                });	}</script><h2>Transactions-log</h2><div class="panel panel-default">  <!-- Default panel contents -->  <div class="panel-heading">Order List</div>  <div class="panel-body">      <div id="message"></div>  </div>  <!-- Table -->  <table class="table" id="orders_table">      <thead>      <tr><th>Sl</th><th>Date Time</th><th>Phone</th><th>Status</th><th width="200">Dtails</th><th>Action</th></tr>      </thead>            <tfoot>      <tr><th>Sl</th><th>Date Time</th><th>Phone</th><th>Status</th><th width="200">Dtails</th><th>Action</th></tr>      </tfoot>  </table></div><script>                    var table=$('#orders_table').DataTable( {                        serverSide: true,                        ajax: {                            url: '<?php echo Config::$base_url ?>/transaction/view_transactions.php',                            type: 'POST'                        },                        strings: {                            "loadingMessage": "Loadding",                             "emptyMessage":"no data"                        }                    } );</script>